import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem/MealItem";
const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Chicken Biryani",
    description: "A Pakistan specialty!",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Karahi",
    description:
      "This is a restaurant-style Pakistani Chicken Karahi recipe that can be prepared quickly and easily with no finicky steps.",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description:
      "A burger is a patty of ground beef grilled and placed between two halves of a bun.",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Pizza",
    description: "Healthy...and green...",
    price: 18.99,
  },
];

const AvailableMeals = () => {
  const mealslist = DUMMY_MEALS.map((meal) => (
    <MealItem
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealslist}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
